/**
 * Plugin_Abstract - Widget plugin interface.
 *
 */
interface Plugin_Abstract
{
	public function __construct($options=null);
	public function setOptions($options);
	public function getOptions();
	public function __toString();
	
	/**
	 * Return the html generated by this plugin.
	 *
	 */
	public function getHtml();
	/**
	 * Return the javascript needed by this plugin.
	 *
	 */
	public function getScript();
	
	/**
	 * Outputs the configuration form
	 *
	 * @return Zend_Form
	 */
	public function getForm();
	
	/**
	 * Validate submitted values.
	 *
	 * @param array $params
	 */
	public function validateForm( $params );
	
	/**
	 * Process the form values.
	 *
	 * @param array $params
	 */
	public function processForm( $params );
	/**
	 * Install the plugin
	 *
	 */
	public function install();
	/**
	 * Uninstall the plugin
	 *
	 */
	public function uninstall();
}
?>